import React, { useEffect, useState } from 'react'
import { Form, Grid } from 'semantic-ui-react'

import { useSubstrate } from './substrate-lib'
import { TxButton } from './substrate-lib/components'

import KittyCards from './KittyCards'

export default function Kitties (props) {
  const { api, keyring } = useSubstrate()
  const { accountPair } = props

  const [kittyCnt, setKittyCnt] = useState(0);
  const [kittyDNAs, setKittyDNAs] = useState([]);
  const [kittyOwners, setKittyOwners] = useState([]);
  const [kitties, setKitties] = useState([])
  const [status, setStatus] = useState('')

  const fetchKittyCnt = () => {
    let unsubscribe;
    api.query.kittiesModule.kittiesCount((cnt) => {
      setKittyCnt(cnt);
      console.log(`kitty count: ${cnt}`);
    }).then(unsub => {
      unsubscribe = unsub;
    }).catch(console.error);
    return () => unsubscribe && unsubscribe();
  };

  const fetchKitties = () => {
    // TODO: 在这里调用 `api.query.kittiesModule.*` 函数去取得猫咪的信息。
    // 你需要取得：
    //   - 共有多少只猫咪
    //   - 每只猫咪的主人是谁
    //   - 每只猫咪的 DNA 是什么，用来组合出它的形态
    let unsubscribe;
    let key_arr = [];
    for (let i=0; i<kittyCnt; i++) {
      key_arr.push(i);
    }    
    api.query.kittiesModule.kitties.multi(key_arr, (kitties) => {
      setKittyDNAs(kitties.map(kitty => kitty.isSome && kitty.unwrap().toU8a()));
    }).then(unsub => {
      unsubscribe = unsub;
    }).catch(console.error);
    return () => unsubscribe && unsubscribe();

  }

  const fetchKittyOwners = () => {
    let unsubscribe;
    let key_arr = [];
    for (let i=0; i<kittyCnt; i++) {
      key_arr.push(i);
    }    
    api.query.kittiesModule.owner.multi(key_arr, (owners) => {
      setKittyOwners(owners.map(owner => owner.isSome && owner.unwrap().toHuman()));
    }).then(unsub => {
      unsubscribe = unsub;
    }).catch(console.error);
    return () => unsubscribe && unsubscribe();
  };

  const populateKitties = () => {
    // TODO: 在这里添加额外的逻辑。你需要组成这样的数组结构：
    //  ```javascript
    //  const kitties = [{
    //    id: 0,
    //    dna: ...,
    //    owner: ...
    //  }, { id: ..., dna: ..., owner: ... }]
    //  ```
    // 这个 kitties 会传入 <KittyCards/> 然后对每只猫咪进行处理
    const kittyIndices = [...Array(kittyCnt).keys()];
    const kitties = kittyIndices.map(ind => ({
      id: ind,
      dna: kittyDNAs[ind],
      owner: kittyOwners[ind]
    }));
    setKitties(kitties);
  }

  useEffect(fetchKittyCnt, [api, keyring]);
  useEffect(fetchKitties, [kittyCnt]);
  useEffect(fetchKittyOwners, [kittyDNAs]);
  useEffect(populateKitties, [kittyOwners])

  return <Grid.Column width={16}>
    <h1>小毛孩</h1>
    <KittyCards kitties={kitties} accountPair={accountPair} setStatus={setStatus}/>
    <Form style={{ margin: '1em 0' }}>
      <Form.Field style={{ textAlign: 'center' }}>
        <TxButton
          accountPair={accountPair} label='创建小毛孩' type='SIGNED-TX' setStatus={setStatus}
          attrs={{
            palletRpc: 'kittiesModule',
            callable: 'create',
            inputParams: [],
            paramFields: []
          }}
        />
      </Form.Field>
    </Form>
    <div style={{ overflowWrap: 'break-word' }}>{status}</div>
  </Grid.Column>
}
